    public static ArrayList<Sahabat> getListData(){
        ArrayList<Sahabat> list = new ArrayList<>();
        for (String[] aData : data) {
            Sahabat sahabat = new Sahabat();
            sahabat.setName(aData[0]);
            sahabat.setShortDescription(aData[1]);
            sahabat.setPhoto(aData[2]);
            sahabat.setDescription(aData[3]);
            sahabat.setBorn(aData[4]);
            sahabat.setDied(aData[5]);
            list.add(sahabat);
        }
        return list;
    }
